let prebtn=document.getElementsByClassName("btncopy"),precode=document.getElementsByClassName("precode");for(let i=0;i<prebtn.length;i++)prebtn[i].addEventListener("click",()=>{const el=document.createElement("textarea");el.value=precode[i].textContent,document.body.appendChild(el),el.select(),document.execCommand("copy"),document.body.removeChild(el)});